import{_ as t,a as s,b as r,c as a,d as i,e as n}from"./chunks/2.BPI78KlM.js";import{_ as o,c as l,o as d,ae as c}from"./chunks/framework.BefrkHQ3.js";const _=JSON.parse('{"title":"Features","description":"","frontmatter":{},"headers":[],"relativePath":"features.md","filePath":"features.md"}'),g={name:"features.md"};function h(m,e,f,u,p,w){return d(),l("div",null,[...e[0]||(e[0]=[c('<h1 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h1><h2 id="file-management" tabindex="-1">File Management <a class="header-anchor" href="#file-management" aria-label="Permalink to &quot;File Management&quot;">​</a></h2><ul><li><strong>Browse and Preview:</strong> Navigate your files and folders with ease. Preview images, videos, and text files directly in the browser.</li><li><strong>File Operations:</strong> Upload, download, move, and delete files and folders.</li><li><strong>Multiple Views:</strong> Switch between a grid view for a visual overview and a detail view for more information.</li><li><strong>Light and Dark Modes:</strong> Choose between light and dark themes to suit your preference.</li><li><strong>Thumbnail Generation:</strong> Thumbnails for images and videos are automatically generated for faster navigation.</li><li><strong>Code Editor:</strong> Edit text-based files with syntax highlighting.</li><li><strong>Search:</strong> Search for files and folders in the current directory, and search for content inside files.</li><li><strong>Context Menu:</strong> A right-click context menu provides quick access to file operations like creating new files and folders.</li><li><strong>Resizable Sidebar:</strong> The sidebar can be resized to your liking.</li><li><strong>Responsive UI:</strong> The user interface is responsive and works well on different screen sizes.</li></ul><table tabindex="0"><thead><tr><th style="text-align:center;"><img src="'+t+'" alt="Dark theme grid view of folders and mixed media files"></th><th style="text-align:center;"><img src="'+s+'" alt="Light theme view of a project directory with files and folders"></th></tr></thead><tbody><tr><td style="text-align:center;">Grid view keeps media heavy folders easy to scan.</td><td style="text-align:center;">Light theme works well for bright shared spaces.</td></tr></tbody></table><table tabindex="0"><thead><tr><th style="text-align:center;"><img src="'+r+'" alt="Dark list view showing metadata like size and modified time"></th><th style="text-align:center;"><img src="'+a+'" alt="Upload manager tracking progress for multiple files"></th></tr></thead><tbody><tr><td style="text-align:center;">Detail view surfaces metadata like file size and timestamps.</td><td style="text-align:center;">Track multi-file uploads with per-item progress feedback.</td></tr></tbody></table><h2 id="user-and-access-management" tabindex="-1">User and Access Management <a class="header-anchor" href="#user-and-access-management" aria-label="Permalink to &quot;User and Access Management&quot;">​</a></h2><ul><li><strong>Local Users:</strong> Create and manage local users with password authentication.</li><li><strong>OIDC Integration:</strong> Authenticate users against an external OpenID Connect provider like Keycloak, Authelia, or Google.</li><li><strong>Admin Settings:</strong> Manage users, including setting permissions and promoting users to administrators.</li><li><strong>Access Control Rules:</strong> Per‑folder rules to enforce read‑only or hidden paths.</li><li><strong>Favorites:</strong> Pin frequently used folders with custom icons for quick access.</li><li><strong>Password Protection:</strong> Protect your entire workspace with a master password.</li><li><strong>Password Changes:</strong> Local users can change their own passwords.</li><li><strong>Secure Access:</strong> Control who has access to your files and folders.</li><li><strong>User Menu:</strong> A user menu in the sidebar provides access to user-specific settings and actions.</li></ul><table tabindex="0"><thead><tr><th style="text-align:center;"><img src="'+i+'" alt="Password creation screen showing Explorer branding and secure access badge"></th><th style="text-align:center;"><img src="'+n+'" alt="Unlock modal prompting for the workspace password"></th></tr></thead><tbody><tr><td style="text-align:center;">Lock the workspace with a master password before anyone can browse.</td><td style="text-align:center;">Re-enter the password to unlock trusted sessions.</td></tr></tbody></table>',8)])])}const k=o(g,[["render",h]]);export{_ as __pageData,k as default};
